export default {
  data() {
    return {
      modal: false,
      modalTitle: "",
      tip: "",
      editCataType: {
        index: "",
        cata: "",
        detail: ""
      },
      columns: [
        {
          title: "序号",
          key: "index",
          width: 100,
          align: "center"
        },
        {
          title: "类别",
          key: "cata",
          width: 100,
          align: "center"
        },
        {
          title: "描述",
          key: "detail",
          // width: 100,
          align: "left",
          render: (h, params) => {
            return h("div", params.row.detail);
          }
        },
        {
          title: "操作",
          key: "operation",
          width: 150,
          align: "center",
          render: (h, params) => {
            return h("div", [
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.editCata(params.row);
                    }
                  }
                },
                "编辑"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  on: {
                    click: () => {
                      // this.remove(params.index);
                    }
                  }
                },
                "删除"
              )
            ]);
          }
        }
      ],
      data: [
        {
          index: 1,
          cata: "厨余垃圾",
          detail: "厨余垃圾是……"
        },
        {
          index: 2,
          cata: "有害垃圾",
          detail: "有害垃圾是……"
        },
        {
          index: 3,
          cata: "可回收物",
          detail: "可回收物是……"
        },
        {
          index: 4,
          cata: "其他垃圾",
          detail: "其他垃圾是……"
        }
      ]
    };
  },
  methods: {
    newCata() {
      this.modal = true;
      this.modalTitle = "新建类别";
    },
    editCata(cata) {
      this.modal = true;
      this.modalTitle = "编辑类别";
      this.editCataType = cata;
    },
    ok() {
      this.$Message.info("新建成功");
    },
    cancel() {}
  }
};
