export default {
  data() {
    return {
      modal: false,
      modalTitle: "",
      tip: "",
      editLocation: {
        cap: "",
        site: "",
        location: ""
      },
      columns: [
        {
          title: "校区",
          key: "cap",
          align: "center"
        },
        {
          title: "投放站点",
          key: "site",
          align: "center"
        },
        {
          title: "具体位置",
          key: "location",
          align: "center"
        },
        {
          title: "操作",
          key: "operation",
          align: "center",
          render: (h, params) => {
            return h("div", [
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      this.editCata(params.row);
                    }
                  }
                },
                "编辑"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  on: {
                    click: () => {
                      this.deleteLocation();
                    }
                  }
                },
                "删除"
              )
            ]);
          }
        }
      ],
      data: [
        {
          cap: "龙子湖",
          site: "1号宿舍楼楼",
          location: "1号楼西门左拐20米"
        },
        {
          cap: "龙子湖",
          site: "20号教学楼",
          location: "20号教学楼一楼"
        },
        {
          cap: "龙子湖",
          site: "新餐厅",
          location: "新餐厅1楼南出口"
        },
        {
          cap: "龙子湖",
          site: "第一报告厅",
          location: "第一报告厅东门"
        }
      ]
    };
  },
  methods: {
    newLocation() {
      this.modal = true;
      this.modalTitle = "新建位置";
    },
    editCata(loca) {
      this.modal = true;
      this.modalTitle = "编辑位置";
      this.editLocation.cap = loca.cap;
      this.editLocation.site = loca.site;
      this.editLocation.location = loca.location;
    },
    handleForm() {
      if (this.modalTitle === "新建位置") {
        this.$Message.info("新建成功");
      } else {
        this.$Message.info("编辑成功");
      }
      this.clearForm();
    },
    cancel() {
      this.clearForm();
    },
    // 清空表单
    clearForm() {
      this.editLocation.cap = "";
      this.editLocation.site = "";
      this.editLocation.location = "";
    },
    deleteLocation() {
      this.$Modal.confirm({
        title: "确定删除这条数据吗？",
        onOk: () => {
          // 发起删除数据的请求
          this.$Message.info("删除成功");
        },
        onCancel: () => {
          // this.$Message.info("取消删除");
        }
      });
    }
  }
};
